import { uiActions } from "./ui-slice"
import { cartActions } from "./cart-slice"

export const fetchCartData = () => {
    return async dispatch => {
        const fetchData = async () => {
            const response = await fetch(
                'https://redux-practice-8e7e6-default-rtdb.firebaseio.com/cart.json'
            )

            if (!response.ok) {
                throw new Error ('Could not fetch data!')
            }

            const data = await response.json()

            return data
        }

        try {
           const cartData = await fetchData()
            dispatch(cartActions.replaceCart(cartData))
        } catch(error) {
            dispatch(uiActions.showNotification({
                status: 'error',
                title: 'Error!',
                message: 'Fetching cart data has failed!!'
              }))
        }

    }
}

export const sendCartData = (cart) => {
    return async (dispatch) => {
        dispatch(
            uiActions.showNotification({
                status: 'pending',
                title: 'Sending',
                message: 'Sending cart data!'
              })

        )

        const sendRequest = async () => {

            const response = await fetch(
                'https://redux-practice-8e7e6-default-rtdb.firebaseio.com/cart.json',
                { 
                  method: 'PUT', 
                  body: JSON.stringify(cart),
                }
              )

              if (!response.ok) {
                throw new Error('Sending cart failed..')
              } 
            }

            try {

                await sendRequest()

                dispatch(uiActions.showNotification({
                  status: 'success',
                  title: 'Success',
                  message: 'Sent cart successfully!'
                }))
            } catch (error) {
                dispatch(uiActions.showNotification({
                    status: 'error',
                    title: 'Error!',
                    message: 'Sending cart data has failed!!'
                  }))
            }


    }
}
